package compute_test

import (
	"fmt"
	"testing"

	"github.com/hashicorp/terraform-plugin-testing/helper/resource"
	"github.com/hashicorp/terraform-provider-google/google/acctest"
)

func TestAccDataSourceGoogleComputeRegionSslPolicy(t *testing.T) {
	t.Parallel()

	policyName := fmt.Sprintf("tf-test-%s", acctest.RandString(t, 10))

	acctest.VcrTest(t, resource.TestCase{
		PreCheck:                 func() { acctest.AccTestPreCheck(t) },
		ProtoV5ProviderFactories: acctest.ProtoV5ProviderFactories(t),
		Steps: []resource.TestStep{
			{
				Config: testAccDataSourceComputeRegionSslPolicy_basic(policyName),
				Check: resource.ComposeTestCheckFunc(
					resource.TestCheckResourceAttr("data.google_compute_region_ssl_policy.policy", "name", policyName),
					resource.TestCheckResourceAttr("data.google_compute_region_ssl_policy.policy", "profile", "MODERN"),
					resource.TestCheckResourceAttr("data.google_compute_region_ssl_policy.policy", "min_tls_version", "TLS_1_0"),
				),
			},
		},
	})
}

func testAccDataSourceComputeRegionSslPolicy_basic(policyName string) string {
	return fmt.Sprintf(`
resource "google_compute_region_ssl_policy" "policy" {
  name            = "%s"
  region          = "us-central1"
  description     = "Generated by TF provider acceptance test"
  min_tls_version = "TLS_1_0"
  profile         = "MODERN"
}

data "google_compute_region_ssl_policy" "policy" {
  name    = google_compute_region_ssl_policy.policy.name
  region  = google_compute_region_ssl_policy.policy.region
}
`, policyName)
}
