package dialogflow_test

import (
	"testing"

	"github.com/hashicorp/terraform-provider-google/google/acctest"
	"github.com/hashicorp/terraform-provider-google/google/envvar"

	"github.com/hashicorp/terraform-plugin-testing/helper/resource"
)

func TestAccDialogflowEnvironment_update(t *testing.T) {
	t.Parallel()

	context := map[string]interface{}{
		"org_id":          envvar.GetTestOrgFromEnv(t),
		"billing_account": envvar.GetTestBillingAccountFromEnv(t),
		"random_suffix":   acctest.RandString(t, 10),
	}

	acctest.VcrTest(t, resource.TestCase{
		PreCheck:                 func() { acctest.AccTestPreCheck(t) },
		ProtoV5ProviderFactories: acctest.ProtoV5ProviderFactories(t),
		Steps: []resource.TestStep{
			{
				Config: testAccDialogflowEnvionment_full1(context),
			},
			{
				ResourceName: "google_dialogflow_environment.foobar",
				RefreshState: true,
			},
			{
				Config: testAccDialogflowEnvionment_full2(context),
			},
			{
				ResourceName: "google_dialogflow_environment.foobar",
				RefreshState: true,
			},
		},
	})
}

func testAccDialogflowEnvionment_full1(context map[string]interface{}) string {
	return acctest.Nprintf(`
	resource "google_project" "agent_project" {
		name = "tf-test-dialogflow-%{random_suffix}"
		project_id = "tf-test-dialogflow-%{random_suffix}"
		org_id     = "%{org_id}"
		billing_account = "%{billing_account}"
		deletion_policy = "DELETE"
	}

	resource "google_project_service" "agent_project" {
		project = google_project.agent_project.project_id
		service = "dialogflow.googleapis.com"
		disable_dependent_services = false
	}

	resource "google_service_account" "dialogflow_service_account" {
		account_id = "tf-test-dialogflow-%{random_suffix}"
	}

	resource "google_project_iam_member" "agent_create" {
		project = google_project_service.agent_project.project
		role    = "roles/dialogflow.admin"
		member  = "serviceAccount:${google_service_account.dialogflow_service_account.email}"
	}

	resource "google_dialogflow_agent" "agent" {
		project = google_project.agent_project.project_id
		display_name = "tf-test-agent-%{random_suffix}"
		default_language_code = "en"
		time_zone = "America/New_York"
		depends_on = [google_project_iam_member.agent_create]
	}

		resource "google_dialogflow_version" "version" {
		depends_on = [google_dialogflow_agent.agent]
		parent = "projects/${google_project.agent_project.project_id}/agent"
		description = "tf-test-description-%{random_suffix}"
	}
		

	resource "google_dialogflow_environment" "foobar" {
		environmentid = "tf-test-%{random_suffix}"
		project = google_project.agent_project.project_id
		description = "tf-test-description-%{random_suffix}"
		agent_version = "projects/${google_project.agent_project.project_id}/locations/global/agent/versions/${google_dialogflow_version.version.name}"
		text_to_speech_settings {
			enable_text_to_speech = true
			sample_rate_hertz = 60
			synthesize_speech_configs  {
				language = "en"
				speaking_rate = 1.0
				pitch = 0
				effects_profile_id = [ "profileid" ]
				voice {
					name = "voice"
					ssml_gender = "SSML_VOICE_GENDER_MALE"
				}
			}
		}
	}
	`, context)
}

func testAccDialogflowEnvionment_full2(context map[string]interface{}) string {
	return acctest.Nprintf(`
	resource "google_project" "agent_project" {
		name = "tf-test-dialogflow-%{random_suffix}"
		project_id = "tf-test-dialogflow-%{random_suffix}"
		org_id     = "%{org_id}"
		billing_account = "%{billing_account}"
		deletion_policy = "DELETE"
	}

	resource "google_project_service" "agent_project" {
		project = google_project.agent_project.project_id
		service = "dialogflow.googleapis.com"
		disable_dependent_services = false
	}

	resource "google_service_account" "dialogflow_service_account" {
		account_id = "tf-test-dialogflow-%{random_suffix}"
	}
	
	resource "google_project_iam_member" "agent_create" {
		project = google_project_service.agent_project.project
		role    = "roles/dialogflow.admin"
		member  = "serviceAccount:${google_service_account.dialogflow_service_account.email}"
	}

	resource "google_dialogflow_agent" "agent" {
		project = google_project.agent_project.project_id
		display_name = "tf-test-agent-%{random_suffix}"
		default_language_code = "en"
		time_zone = "America/New_York"
		depends_on = [google_project_iam_member.agent_create]
	}
	resource "google_dialogflow_version" "version" {
		depends_on = [google_dialogflow_agent.agent]
		parent = "projects/${google_project.agent_project.project_id}/agent"
		description = "tf-test-description-%{random_suffix}"
	}

	resource "google_dialogflow_version" "version_changed" {
		depends_on = [google_dialogflow_agent.agent]
		parent = "projects/${google_project.agent_project.project_id}/agent"
		description = "tf-test-version-%{random_suffix}2"
	}
		

	resource "google_dialogflow_environment" "foobar" {
		environmentid = "tf-test-%{random_suffix}"
		project = google_project.agent_project.project_id
		description = "tf-test-description-%{random_suffix}-diff"
		agent_version = "projects/${google_project.agent_project.project_id}/locations/global/agent/versions/${google_dialogflow_version.version_changed.name}"
		text_to_speech_settings {
			enable_text_to_speech = false
			sample_rate_hertz = 61
			synthesize_speech_configs  {
				language = "fr"
				speaking_rate = 1.1
				pitch = 1.0
				effects_profile_id = [ "profileid2" ]
				voice {
					name = "voice-changed"
					ssml_gender = "SSML_VOICE_GENDER_FEMALE"
				}
			}
		}
	}
	`, context)
}
